css:
.ul-one {
  display: flex;
  flex: 1 1 auto;
  margin: 0;
  padding: 0;
  justify-content: flex-end;
  border: 1px solid #111
}

.ul-one li {
  list-style: none;
  text-decoration: none;
  color: #111;
  position: relative;
  transition: all 0.4s ease;
  padding: 0 0.5em 0.25em 0.5em;
  cursor: pointer;
}
